  `user_pass` varchar(100) DEFAULT NULL,
  `user_level` varchar(10) DEFAULT NULL,
  `dev` varchar(10) DEFAULT NULL,
  `sal_ex` varchar(10) DEFAULT '0',
  `sal_dep` varchar(10) DEFAULT '0',
  `COMCODE` varchar(5) DEFAULT NULL,
  PRIMARY KEY (`id`)
